mapper.update:
UPDATE review
SET title = #{title}, content = #{content}
where no = #{no}

UPDATE routine
SET difficulty = #{difficulty}
WHERE review_no = #{reviewNo}
AND video_no = #{videoNo}

mapper.select:
SELECT no, review_no reviewNo, user_id userId, comment, date
FROM comment
WHERE review_no = #{reviewNo};

mapper.update:
UPDATE comment
SET comment = #{comment}
WHERE no = #{no}
